%EECE 570- RAFEEF GARBI %Assignment 3%% **********************************************% Question 2 Student File% Demonstrating the use of the radon transform% for reconstructing an image from projection% or attentuation profileclear all; close all; clc;%% MRI IMAGEload mriD=squeeze(D);obj=im2double(D(:,:,13));%% COLLECT PROJECTIONSnum_angles=150;angles = linspace(0,180,num_angles);R = radon(obj,angles);% RECONSTRUCT FROM PROJECTIONSinterpolators = {'nearest';'linear';'spline';'pchip';'cubic';'v5cubic'};filters = {'none';'Ram-Lak';'Shepp-Logan';'Cosine';'Hamming';'Hann'};bestMse = 9999999;bestSNR = 0;bestMse_i = 0;bestMse_j = 0;bestSNR_i = 0;bestSNR_j = 0;for i=1:6    for j=1:6        I = iradon(R,angles,char(interpolators(i)),char(filters(j)),128); %back projection reconstruction        subplot(6,6,6*(i-1)+j);imshow(I,[]); title(strcat(char(interpolators(i)),'-',char(filters(j))));        SE = (I-obj).^2;        MSE = mean(SE(:)); %Mean Squared Error        if MSE < bestMse            bestMse = MSE;            bestMse_i = i;            bestMse_j = j;        end        SNR = 20*log(norm(obj,'fro')/norm(obj-I,'fro')); %Signal-to-Noise Ratio        if SNR > bestSNR            bestSNR = SNR;            bestSNR_i = i;            bestSNR_j = j;        end    endend